## 简单 CURD 

```

// UserInfo 用户信息
type UserInfo struct {
	ID     uint
	Name   string
	Age    int
	Gender string
	Hobby  string
}

func main() {
	db, err := gorm.Open("sqlite3", "db/gorm.db")
	if err != nil {
		fmt.Println("%v\n", err)
		panic(err)
	}
	defer db.Close()
	// 自动迁移
	db.AutoMigrate(&UserInfo{})


  // ################
	//  添加数据
	// ################  
	// 定义对象
	u1 := UserInfo{1, "七米", 18,"男", "篮球"}
	u2 := UserInfo{2, "沙河娜扎", 23,"女", "足球"}

	// 创建记录
